import { useState, useRef, useEffect } from "react"
import { useRouter } from "next/navigation"
import { Button } from "@/components/ui/button"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card"
import { Label } from "@/components/ui/label"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Textarea } from "@/components/ui/textarea"
import { toast } from "@/components/ui/use-toast"
import { Loader2, Save, Send, Clock, RotateCcw } from "lucide-react"
import { testPrompt, savePromptTest } from "@/app/actions"

interface PromptTesterProps {
  promptId: number
  promptText: string
  systemMessage: string | null
  variables: string[]
}

export function PromptTester({ promptId, promptText, systemMessage, variables }: PromptTesterProps) {
  const router = useRouter()
  const [variableValues, setVariableValues] = useState<Record<string, string>>({})
  const [isLoading, setIsLoading] = useState(false)
  const [testResult, setTestResult] = useState<any>(null)
  const [testHistory, setTestHistory] = useState<any[]>([])
  const [activeTab, setActiveTab] = useState("test")
  const responseRef = useRef<HTMLDivElement>(null)

  // Initialize variable values
  useEffect(() => {
    const initialValues: Record<string, string> = {}
    variables.forEach((variable) => {
      initialValues[variable] = ""
    })
    setVariableValues(initialValues)
  }, [variables])

  // Function to handle variable input changes
  const handleVariableChange = (variable: string, value: string) => {
    setVariableValues((prev) => ({
      ...prev,
      [variable]: value,
    }))
  }

  // Function to generate the final prompt with variables replaced
  const generateFinalPrompt = () => {
    let finalPrompt = promptText
    for (const [key, value] of Object.entries(variableValues)) {
      finalPrompt = finalPrompt.replace(new RegExp(`\\{\\{${key}\\}\\}`, "g"), value || `[${key}]`)
    }
    return finalPrompt
  }

  // Function to test the prompt
  const handleTest = async () => {
    setIsLoading(true)
    setTestResult(null)

    try {
      // Check if all variables have values
      const missingVariables = variables.filter((v) => !variableValues[v])
      if (missingVariables.length > 0) {
        toast({
          title: "Missing variables",
          description: `Please provide values for: ${missingVariables.join(", ")}`,
          variant: "destructive",
        })
        setIsLoading(false)
        return
      }

      const result = await testPrompt({
        promptId,
        prompt: promptText,
        systemMessage,
        variables: variableValues,
      })

      if (result.success && result.data) {
        setTestResult(result.data)
        // Add to history
        setTestHistory((prev) => [result.data, ...prev].slice(0, 10))
        setActiveTab("result")
      } else {
        toast({
          title: "Test failed",
          description: result.error || "Failed to test prompt. Please try again.",
          variant: "destructive",
        })
      }
    } catch (error) {
      console.error("Error testing prompt:", error)
      toast({
        title: "Error",
        description: "An unexpected error occurred. Please try again.",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  // Function to save test result
  const handleSaveTest = async () => {
    if (!testResult) return

    try {
      const result = await savePromptTest(testResult)

      if (result.success) {
        toast({
          title: "Test saved",
          description: "The test result has been saved successfully.",
        })
      } else {
        toast({
          title: "Save failed",
          description: result.error || "Failed to save test. Please try again.",
          variant: "destructive",
        })
      }
    } catch (error) {
      console.error("Error saving test:", error)
      toast({
        title: "Error",
        description: "An unexpected error occurred. Please try again.",
        variant: "destructive",
      })
    }
  }

  // Function to restore a test from history
  const handleRestoreTest = (test: any) => {
    const restoredVariables: Record<string, string> = {}
    Object.entries(test.variables).forEach(([key, value]) => {
      restoredVariables[key] = value as string
    })

    setVariableValues(restoredVariables)
    setActiveTab("test")

    toast({
      title: "Test restored",
      description: "Variable values have been restored from the selected test.",
    })
  }

  // Function to format response time
  const formatResponseTime = (ms: number) => {
    if (ms < 1000) return `${ms}ms`
    return `${(ms / 1000).toFixed(2)}s`
  }

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle>Test Prompt</CardTitle>
        <CardDescription>Fill in the variables and test how the prompt performs with the AI.</CardDescription>
      </CardHeader>
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="mx-6">
          <TabsTrigger value="test">Test</TabsTrigger>
          <TabsTrigger value="result" disabled={!testResult}>
            Result
          </TabsTrigger>
          <TabsTrigger value="history" disabled={testHistory.length === 0}>
            History
          </TabsTrigger>
        </TabsList>

        <CardContent>
          <TabsContent value="test" className="space-y-4 mt-4">
            {variables.length > 0 ? (
              <div className="space-y-4">
                <h3 className="text-sm font-medium">Variables</h3>
                {variables.map((variable) => (
                  <div key={variable} className="grid gap-2">
                    <Label htmlFor={variable}>{variable}</Label>
                    <Textarea
                      id={variable}
                      placeholder={`Enter value for ${variable}`}
                      value={variableValues[variable] || ""}
                      onChange={(e) => handleVariableChange(variable, e.target.value)}
                      className="min-h-[80px]"
                    />
                  </div>
                ))}
              </div>
            ) : (
              <div className="bg-muted p-4 rounded-md">
                <p className="text-sm text-muted-foreground">This prompt doesn't contain any variables.</p>
              </div>
            )}

            <div className="space-y-2">
              <h3 className="text-sm font-medium">Preview</h3>
              <div className="bg-muted p-4 rounded-md whitespace-pre-wrap text-sm">{generateFinalPrompt()}</div>
            </div>

            <Button onClick={handleTest} disabled={isLoading} className="w-full">
              {isLoading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Testing...
                </>
              ) : (
                <>
                  <Send className="mr-2 h-4 w-4" />
                  Test Prompt
                </>
              )}
            </Button>
          </TabsContent>

          <TabsContent value="result" className="space-y-4 mt-4">
            {testResult && (
              <>
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <h3 className="text-sm font-medium">AI Response</h3>
                    <div className="flex items-center text-xs text-muted-foreground">
                      <Clock className="mr-1 h-3 w-3" />
                      {formatResponseTime(testResult.responseTime)}
                    </div>
                  </div>
                  <div
                    ref={responseRef}
                    className="bg-muted p-4 rounded-md whitespace-pre-wrap text-sm max-h-[400px] overflow-y-auto"
                  >
                    {testResult.response}
                  </div>
                </div>

                <div className="space-y-2">
                  <h3 className="text-sm font-medium">Final Prompt</h3>
                  <div className="bg-muted p-4 rounded-md whitespace-pre-wrap text-sm max-h-[200px] overflow-y-auto">
                    {testResult.finalPrompt}
                  </div>
                </div>

                <Button onClick={handleSaveTest} variant="outline" className="w-full">
                  <Save className="mr-2 h-4 w-4" />
                  Save Test Result
                </Button>
              </>
            )}
          </TabsContent>

          <TabsContent value="history" className="space-y-4 mt-4">
            {testHistory.length > 0 ? (
              <div className="space-y-4">
                {testHistory.map((test, index) => (
                  <div key={index} className="border rounded-md p-4 space-y-2">
                    <div className="flex items-center justify-between">
                      <h4 className="text-sm font-medium">Test {index + 1}</h4>
                      <div className="flex items-center gap-2">
                        <span className="text-xs text-muted-foreground">
                          {new Date(test.timestamp).toLocaleString()}
                        </span>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleRestoreTest(test)}
                          title="Restore this test"
                        >
                          <RotateCcw className="h-3 w-3" />
                        </Button>
                      </div>
                    </div>
                    <div className="text-sm line-clamp-2 text-muted-foreground">
                      {test.response.substring(0, 100)}...
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="bg-muted p-4 rounded-md">
                <p className="text-sm text-muted-foreground">No test history available.</p>
              </div>
            )}
          </TabsContent>
        </CardContent>
      </Tabs>

      <CardFooter className="flex justify-between">
        <Button variant="outline" onClick={() => router.back()}>
          Back
        </Button>
      </CardFooter>
    </Card>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Badge } from "@/components/ui/badge"
import { Play, Copy, Check, Sparkles, Loader2, Variable, MessageSquare, Save, Download } from "lucide-react"
import { toast } from "@/components/ui/use-toast"
import { extractVariables } from "@/lib/validation-utils"
import { testPrompt, savePromptTest } from "@/app/actions"
import { Skeleton } from "@/components/ui/skeleton"
import { Switch } from "@/components/ui/switch"
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip"

interface PromptTestingInterfaceProps {
  prompt: {
    id: number
    name: string
    prompt: string
    systemMessage: string | null
  }
}

interface VariableValues {
  [key: string]: string
}

interface TestResult {
  id?: number
  promptId: number
  variables: VariableValues
  finalPrompt: string
  response: string
  responseTime: number
  timestamp: string
  saved?: boolean
}

export function PromptTestingInterface({ prompt }: PromptTestingInterfaceProps) {
  const [variables, setVariables] = useState<string[]>([])
  const [variableValues, setVariableValues] = useState<VariableValues>({})
  const [finalPrompt, setFinalPrompt] = useState("")
  const [isGenerating, setIsGenerating] = useState(false)
  const [isSaving, setIsSaving] = useState(false)
  const [copied, setCopied] = useState(false)
  const [activeTab, setActiveTab] = useState("variables")
  const [testResult, setTestResult] = useState<TestResult | null>(null)
  const [useSystemMessage, setUseSystemMessage] = useState(true)
  const [recentTests, setRecentTests] = useState<TestResult[]>([])
  const [isLoadingTests, setIsLoadingTests] = useState(false)

  // Extract variables from the prompt
  useEffect(() => {
    const extractedVars = extractVariables(prompt.prompt)
    setVariables(extractedVars)

    // Initialize variables with empty strings
    const initialVars: VariableValues = {}
    extractedVars.forEach((variable) => {
      initialVars[variable] = ""
    })
    setVariableValues(initialVars)
  }, [prompt.prompt])

  const handleVariableChange = (variable: string, value: string) => {
    setVariableValues((prev) => ({ ...prev, [variable]: value }))
  }

  const generateFinalPrompt = () => {
    let result = prompt.prompt

    // Replace variables in the prompt
    for (const [key, value] of Object.entries(variableValues)) {
      const regex = new RegExp(`\\{\\{${key}\\}\\}`, "g")
      result = result.replace(regex, value || `{{${key}}}`)
    }

    setFinalPrompt(result)
    return result
  }

  const handleTest = async () => {
    setIsGenerating(true)
    setActiveTab("result")

    try {
      const finalPromptText = generateFinalPrompt()

      // Check if any variables are empty
      const emptyVars = Object.entries(variableValues).filter(([_, value]) => !value)
      if (emptyVars.length > 0) {
        toast({
          title: "Warning",
          description: "Some variables are empty. They will remain as placeholders in the test.",
          variant: "default",
        })
      }

      // Call the API to test the prompt
      const result = await testPrompt({
        promptId: prompt.id,
        prompt: finalPromptText,
        systemMessage: useSystemMessage ? prompt.systemMessage : null,
        variables: variableValues,
      })

      if (result.success) {
        setTestResult(result.data)
        // Add to recent tests
        setRecentTests((prev) => [result.data, ...prev].slice(0, 5))
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to test prompt",
          variant: "destructive",
        })
      }
    } catch (error) {
      console.error("Error testing prompt:", error)
      toast({
        title: "Error",
        description: "Failed to test prompt",
        variant: "destructive",
      })
    } finally {
      setIsGenerating(false)
    }
  }

  const handleSaveTest = async () => {
    if (!testResult) return

    setIsSaving(true)
    try {
      const result = await savePromptTest(testResult)

      if (result.success) {
        setTestResult((prev) => (prev ? { ...prev, saved: true, id: result.data.id } : null))
        toast({
          title: "Test saved",
          description: "The test result has been saved successfully.",
        })
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to save test",
          variant: "destructive",
        })
      }
    } catch (error) {
      console.error("Error saving test:", error)
      toast({
        title: "Error",
        description: "Failed to save test",
        variant: "destructive",
      })
    } finally {
      setIsSaving(false)
    }
  }

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text)
      setCopied(true)
      toast({
        title: "Copied!",
        description: "Text copied to clipboard",
      })
      setTimeout(() => setCopied(false), 2000)
    } catch (error) {
      console.error("Failed to copy text:", error)
      toast({
        title: "Error",
        description: "Failed to copy to clipboard",
        variant: "destructive",
      })
    }
  }

  const fillRandomValues = () => {
    const filledVars: VariableValues = {}
    variables.forEach((variable) => {
      // Generate placeholder text based on variable name
      if (variable.includes("name")) {
        filledVars[variable] = "John Smith"
      } else if (variable.includes("date")) {
        filledVars[variable] = new Date().toLocaleDateString()
      } else if (variable.includes("email")) {
        filledVars[variable] = "john.smith@example.com"
      } else if (variable.includes("contract") || variable.includes("text")) {
        filledVars[variable] =
          "This agreement is made between Party A and Party B for the provision of legal services..."
      } else {
        filledVars[variable] = `Sample ${variable} value`
      }
    })
    setVariableValues(filledVars)
  }

  const exportTestResult = () => {
    if (!testResult) return

    try {
      const data = JSON.stringify(testResult, null, 2)
      const blob = new Blob([data], { type: "application/json" })
      const url = URL.createObjectURL(blob)

      const link = document.createElement("a")
      link.href = url
      link.download = `prompt-test-${new Date().toISOString().slice(0, 19).replace(/:/g, "-")}.json`
      document.body.appendChild(link)
      link.click()

      document.body.removeChild(link)
      URL.revokeObjectURL(url)

      toast({
        title: "Exported",
        description: "Test result has been exported as JSON",
      })
    } catch (error) {
      console.error("Error exporting test result:", error)
      toast({
        title: "Error",
        description: "Failed to export test result",
        variant: "destructive",
      })
    }
  }

  return (
    <TooltipProvider>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Sparkles className="h-5 w-5 text-primary" />
            Test Prompt
          </CardTitle>
          <CardDescription>Test your prompt with different variable values</CardDescription>
        </CardHeader>

        <CardContent>
          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList>
              <TabsTrigger value="variables" className="gap-1">
                <Variable className="h-4 w-4" />
                Variables
              </TabsTrigger>
              <TabsTrigger value="preview" disabled={!finalPrompt}>
                Preview
              </TabsTrigger>
              <TabsTrigger value="result" disabled={!testResult && !isGenerating}>
                Result
              </TabsTrigger>
              <TabsTrigger value="history">History</TabsTrigger>
            </TabsList>

            <TabsContent value="variables" className="pt-4 space-y-4">
              {variables.length > 0 ? (
                <>
                  <div className="flex justify-between items-center">
                    <h3 className="text-sm font-medium">Fill in the variables</h3>
                    <Button variant="outline" size="sm" onClick={fillRandomValues} className="h-8 px-2 gap-1">
                      <Sparkles className="h-3.5 w-3.5" />
                      Fill Sample Data
                    </Button>
                  </div>

                  <div className="space-y-4">
                    {variables.map((variable) => (
                      <div key={variable} className="space-y-2">
                        <Label htmlFor={variable} className="flex items-center gap-2">
                          {variable}
                          <Badge variant="outline" className="font-normal">
                            Variable
                          </Badge>
                        </Label>
                        <Input
                          id={variable}
                          placeholder={`Enter value for ${variable}`}
                          value={variableValues[variable] || ""}
                          onChange={(e) => handleVariableChange(variable, e.target.value)}
                        />
                      </div>
                    ))}
                  </div>

                  {prompt.systemMessage && (
                    <div className="flex items-center space-x-2 pt-2">
                      <Switch
                        id="use-system-message"
                        checked={useSystemMessage}
                        onCheckedChange={setUseSystemMessage}
                      />
                      <Label htmlFor="use-system-message" className="flex items-center gap-2">
                        <MessageSquare className="h-4 w-4" />
                        Include system message
                      </Label>
                    </div>
                  )}
                </>
              ) : (
                <div className="text-center py-8">
                  <p className="text-muted-foreground">No variables found in this prompt. You can still test it.</p>
                </div>
              )}
            </TabsContent>

            <TabsContent value="preview" className="pt-4 space-y-4">
              <div>
                <h3 className="text-sm font-medium mb-2 flex justify-between items-center">
                  <span>Final Prompt</span>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => copyToClipboard(finalPrompt)}
                        className="h-8 gap-1"
                      >
                        {copied ? <Check className="h-3.5 w-3.5" /> : <Copy className="h-3.5 w-3.5" />}
                        {copied ? "Copied!" : "Copy"}
                      </Button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p>Copy to clipboard</p>
                    </TooltipContent>
                  </Tooltip>
                </h3>
                <div className="bg-muted/50 p-4 rounded-md whitespace-pre-wrap border">{finalPrompt}</div>
              </div>

              {useSystemMessage && prompt.systemMessage && (
                <div>
                  <h3 className="text-sm font-medium mb-2">System Message</h3>
                  <div className="bg-muted/50 p-4 rounded-md whitespace-pre-wrap border">{prompt.systemMessage}</div>
                </div>
              )}
            </TabsContent>

            <TabsContent value="result" className="pt-4 space-y-4">
              {isGenerating ? (
                <div className="flex flex-col items-center justify-center py-12">
                  <Loader2 className="h-8 w-8 animate-spin text-primary mb-4" />
                  <p className="text-muted-foreground">Generating response...</p>
                </div>
              ) : testResult ? (
                <>
                  <div>
                    <h3 className="text-sm font-medium mb-2 flex justify-between items-center">
                      <span>AI Response</span>
                      <div className="flex gap-2">
                        <Tooltip>
                          <TooltipTrigger asChild>
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => copyToClipboard(testResult.response)}
                              className="h-8 gap-1"
                            >
                              {copied ? <Check className="h-3.5 w-3.5" /> : <Copy className="h-3.5 w-3.5" />}
                              {copied ? "Copied!" : "Copy"}
                            </Button>
                          </TooltipTrigger>
                          <TooltipContent>
                            <p>Copy to clipboard</p>
                          </TooltipContent>
                        </Tooltip>

                        <Tooltip>
                          <TooltipTrigger asChild>
                            <Button variant="outline" size="sm" onClick={exportTestResult} className="h-8 gap-1">
                              <Download className="h-3.5 w-3.5" />
                              Export
                            </Button>
                          </TooltipTrigger>
                          <TooltipContent>
                            <p>Export as JSON</p>
                          </TooltipContent>
                        </Tooltip>
                      </div>
                    </h3>
                    <div className="bg-muted/50 p-4 rounded-md whitespace-pre-wrap border">{testResult.response}</div>
                  </div>

                  <div className="flex justify-between items-center text-sm text-muted-foreground">
                    <div>Response time: {testResult.responseTime}ms</div>
                    <div>{new Date(testResult.timestamp).toLocaleString()}</div>
                  </div>
                </>
              ) : (
                <div className="text-center py-8">
                  <p className="text-muted-foreground">No test results yet. Run a test to see results here.</p>
                </div>
              )}
            </TabsContent>

            <TabsContent value="history" className="pt-4">
              {isLoadingTests ? (
                <div className="space-y-4">
                  {Array.from({ length: 3 }).map((_, i) => (
                    <Card key={i} className="p-4">
                      <Skeleton className="h-4 w-1/3 mb-2" />
                      <Skeleton className="h-20 w-full" />
                      <div className="flex justify-between mt-2">
                        <Skeleton className="h-3 w-24" />
                        <Skeleton className="h-3 w-32" />
                      </div>
                    </Card>
                  ))}
                </div>
              ) : recentTests.length > 0 ? (
                <div className="space-y-4">
                  {recentTests.map((test, index) => (
                    <Card key={index} className="p-4 hover:shadow-sm transition-shadow">
                      <div className="flex justify-between items-start mb-2">
                        <h4 className="text-sm font-medium">Test {test.id || index + 1}</h4>
                        <Badge variant={test.saved ? "default" : "outline"} className="text-xs">
                          {test.saved ? "Saved" : "Unsaved"}
                        </Badge>
                      </div>
                      <div className="text-sm line-clamp-3 mb-2">{test.response}</div>
                      <div className="flex justify-between items-center text-xs text-muted-foreground">
                        <div>Response time: {test.responseTime}ms</div>
                        <div>{new Date(test.timestamp).toLocaleString()}</div>
                      </div>
                      <div className="flex justify-end mt-2 gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          className="h-7 text-xs"
                          onClick={() => {
                            setTestResult(test)
                            setActiveTab("result")
                          }}
                        >
                          View
                        </Button>
                      </div>
                    </Card>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
                  <p className="text-muted-foreground">No test history available.</p>
                </div>
              )}
            </TabsContent>
          </Tabs>
        </CardContent>

        <CardFooter className="flex justify-between">
          <Button
            variant="outline"
            onClick={() => {
              generateFinalPrompt()
              setActiveTab("preview")
            }}
            disabled={isGenerating}
          >
            Preview
          </Button>

          <div className="flex gap-2">
            {testResult && !testResult.saved && (
              <Button variant="outline" onClick={handleSaveTest} disabled={isSaving} className="gap-1">
                {isSaving ? <Loader2 className="h-4 w-4 animate-spin" /> : <Save className="h-4 w-4" />}
                {isSaving ? "Saving..." : "Save Result"}
              </Button>
            )}

            <Button onClick={handleTest} disabled={isGenerating} className="gap-1">
              {isGenerating ? (
                <>
                  <Loader2 className="h-4 w-4 animate-spin" />
                  Testing...
                </>
              ) : (
                <>
                  <Play className="h-4 w-4" />
                  Test Prompt
                </>
              )}
            </Button>
          </div>
        </CardFooter>
      </Card>
    </TooltipProvider>
  )
}

"use client"

import { useState, useRef, useEffect } from "react"
import { X, Plus, TagIcon, Loader2 } from "lucide-react"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { toast } from "@/components/ui/use-toast"
import { cn } from "@/lib/utils"
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
  CommandSeparator,
} from "@/components/ui/command"
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover"
import { createTag, getAllTags } from "@/app/actions"

interface TagManagementProps {
  selectedTags: string[]
  onTagsChange: (tags: string[]) => void
  className?: string
  disabled?: boolean
}

export function TagManagement({ selectedTags, onTagsChange, className, disabled = false }: TagManagementProps) {
  const [open, setOpen] = useState(false)
  const [inputValue, setInputValue] = useState("")
  const [availableTags, setAvailableTags] = useState<string[]>([])
  const [isLoading, setIsLoading] = useState(false)
  const [isCreatingTag, setIsCreatingTag] = useState(false)
  const inputRef = useRef<HTMLInputElement>(null)

  // Load available tags
  useEffect(() => {
    const loadTags = async () => {
      setIsLoading(true)
      try {
        const tags = await getAllTags()
        setAvailableTags(tags)
      } catch (error) {
        console.error("Error loading tags:", error)
        toast({
          title: "Error",
          description: "Failed to load tags",
          variant: "destructive",
        })
      } finally {
        setIsLoading(false)
      }
    }

    loadTags()
  }, [])

  const handleSelect = (tag: string) => {
    if (!selectedTags.includes(tag)) {
      const newTags = [...selectedTags, tag]
      onTagsChange(newTags)
    }
    setInputValue("")
    setOpen(false)
    inputRef.current?.focus()
  }

  const handleRemove = (tag: string) => {
    const newTags = selectedTags.filter((t) => t !== tag)
    onTagsChange(newTags)
  }

  const handleCreateTag = async () => {
    if (!inputValue.trim()) return

    setIsCreatingTag(true)
    try {
      const newTag = inputValue.trim()
      const result = await createTag(newTag)

      if (result.success) {
        setAvailableTags((prev) => [...prev, newTag])
        handleSelect(newTag)
        toast({
          title: "Tag created",
          description: `Tag "${newTag}" has been created.`,
        })
      } else {
        toast({
          title: "Error",
          description: result.error || "Failed to create tag",
          variant: "destructive",
        })
      }
    } catch (error) {
      console.error("Error creating tag:", error)
      toast({
        title: "Error",
        description: "Failed to create tag",
        variant: "destructive",
      })
    } finally {
      setIsCreatingTag(false)
      setInputValue("")
    }
  }

  const filteredTags = availableTags.filter(
    (tag) => tag.toLowerCase().includes(inputValue.toLowerCase()) && !selectedTags.includes(tag),
  )

  return (
    <div className={cn("space-y-2", className)}>
      <div className="flex flex-wrap gap-2 min-h-9">
        {selectedTags.map((tag) => (
          <Badge key={tag} variant="secondary" className="px-2 py-1 gap-1 text-xs">
            <TagIcon className="h-3 w-3" />
            <span className="max-w-[100px] sm:max-w-[200px] truncate">{tag}</span>
            <button
              className="ml-1 text-muted-foreground hover:text-foreground rounded-full"
              onClick={() => handleRemove(tag)}
              disabled={disabled}
            >
              <X className="h-3 w-3" />
            </button>
          </Badge>
        ))}

        {!disabled && (
          <Popover open={open} onOpenChange={setOpen}>
            <PopoverTrigger asChild>
              <Button variant="outline" size="sm" className="h-9 gap-1 border-dashed" disabled={disabled}>
                <Plus className="h-3.5 w-3.5" />
                Add Tag
              </Button>
            </PopoverTrigger>
            <PopoverContent className="p-0 w-[200px]" align="start">
              <Command>
                <CommandInput
                  placeholder="Search tags..."
                  value={inputValue}
                  onValueChange={setInputValue}
                  ref={inputRef}
                  className="h-9"
                />

                <CommandList>
                  {isLoading ? (
                    <div className="py-6 text-center">
                      <Loader2 className="h-4 w-4 animate-spin mx-auto" />
                      <p className="text-sm text-muted-foreground mt-2">Loading tags...</p>
                    </div>
                  ) : (
                    <>
                      <CommandEmpty>
                        {inputValue.trim() !== "" ? (
                          <div className="py-3 px-2">
                            <p className="text-sm text-muted-foreground mb-2">Create new tag:</p>
                            <Button
                              size="sm"
                              className="w-full justify-start gap-1 h-8"
                              onClick={handleCreateTag}
                              disabled={isCreatingTag}
                            >
                              {isCreatingTag ? (
                                <Loader2 className="h-3.5 w-3.5 animate-spin" />
                              ) : (
                                <Plus className="h-3.5 w-3.5" />
                              )}
                              "{inputValue}"
                            </Button>
                          </div>
                        ) : (
                          <p className="py-6 text-center text-sm">No tags found.</p>
                        )}
                      </CommandEmpty>

                      {filteredTags.length > 0 && (
                        <CommandGroup heading="Available Tags">
                          {filteredTags.map((tag) => (
                            <CommandItem key={tag} value={tag} onSelect={() => handleSelect(tag)} className="gap-2">
                              <TagIcon className="h-3.5 w-3.5" />
                              {tag}
                            </CommandItem>
                          ))}
                        </CommandGroup>
                      )}

                      {inputValue.trim() !== "" && filteredTags.length > 0 && (
                        <>
                          <CommandSeparator />
                          <CommandGroup>
                            <CommandItem onSelect={handleCreateTag} className="gap-1">
                              <Plus className="h-3.5 w-3.5" />
                              Create "{inputValue}"
                            </CommandItem>
                          </CommandGroup>
                        </>
                      )}
                    </>
                  )}
                </CommandList>
              </Command>
            </PopoverContent>
          </Popover>
        )}
      </div>
    </div>
  )
}

"use client"

import type React from "react"

import { useState } from "react"
import { X, Plus } from "lucide-react"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { useTags } from "@/components/tags-provider"

interface TagsInputProps {
  promptId: number
  className?: string
}

export function TagsInput({ promptId, className }: TagsInputProps) {
  const { getPromptTags, addTag, removeTag, allTags } = useTags()
  const [newTag, setNewTag] = useState("")
  const [isAdding, setIsAdding] = useState(false)

  const promptTags = getPromptTags(promptId)

  const handleAddTag = () => {
    if (newTag.trim()) {
      addTag(promptId, newTag.trim())
      setNewTag("")
      setIsAdding(false)
    }
  }

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      e.preventDefault()
      handleAddTag()
    } else if (e.key === "Escape") {
      setIsAdding(false)
      setNewTag("")
    }
  }

  return (
    <div className={`flex flex-wrap gap-2 ${className}`}>
      {promptTags.map((tag) => (
        <Badge key={tag} variant="secondary" className="text-xs">
          {tag}
          <button
            onClick={() => removeTag(promptId, tag)}
            className="ml-1 rounded-full hover:bg-muted"
            aria-label={`Remove ${tag} tag`}
          >
            <X className="h-3 w-3" />
          </button>
        </Badge>
      ))}

      {isAdding ? (
        <div className="flex items-center gap-1">
          <Input
            type="text"
            value={newTag}
            onChange={(e) => setNewTag(e.target.value)}
            onKeyDown={handleKeyDown}
            className="h-7 w-32 text-xs"
            placeholder="Add tag..."
            autoFocus
          />
          <Button size="sm" variant="ghost" className="h-7 w-7 p-0" onClick={handleAddTag}>
            <Plus className="h-3 w-3" />
            <span className="sr-only">Add Tag</span>
          </Button>
        </div>
      ) : (
        <Button size="sm" variant="outline" className="h-6 text-xs" onClick={() => setIsAdding(true)}>
          <Plus className="mr-1 h-3 w-3" />
          Add Tag
        </Button>
      )}
    </div>
  )
}

